import { createTheme } from "@mui/material/styles";
import { red } from "@mui/material/colors";

// Create a theme instance.
const theme = createTheme({
  overrides: {
    MuiTab: {
      root: {
        fontColor: "#f44336",
      },
    },
  },
  palette: {
    primary: {
      main: "#bc5334",
    },
    secondary: {
      main: "#deb27f",
    },
    text: {
      primary: "#2b1611",
    },
    // background:{
    //   default: "#331B1A",
    // },
    error: {
      main: "#f44336",
    },
  },
  typography: {},
  spacing: 10,
});

theme.spacing(2);

export default theme;
